package ac.id.binus.labux.model;

public class MangaRelease {
    private String title;
    private String description;
    private String chapter;
    private String imageResource;

    public MangaRelease(String title, String description, String chapter, String imageResource) {
        this.title = title;
        this.description = description;
        this.chapter = chapter;
        this.imageResource = imageResource;
    }

    // Getters
    public String getTitle() { return title; }
    public String getDescription() { return description; }
    public String getChapter() { return chapter; }
    public String getImageResource() { return imageResource; }

    // Setters
    public void setTitle(String title) { this.title = title; }
    public void setDescription(String description) { this.description = description; }
    public void setChapter(String chapter) { this.chapter = chapter; }
    public void setImageResource(String imageResource) { this.imageResource = imageResource; }
}

package ac.id.binus.labux.model;

public class Review {
    private String title;
    private String subtitle;
    private String description;
    private int rating;
    private String imageResource;

    public Review(String title, String subtitle, String description, int rating, String imageResource) {
        this.title = title;
        this.subtitle = subtitle;
        this.description = description;
        this.rating = rating;
        this.imageResource = imageResource;
    }

    // Getters
    public String getTitle() { return title; }
    public String getSubtitle() { return subtitle; }
    public String getDescription() { return description; }
    public int getRating() { return rating; }
    public String getImageResource() { return imageResource; }

    // Setters
    public void setTitle(String title) { this.title = title; }
    public void setSubtitle(String subtitle) { this.subtitle = subtitle; }
    public void setDescription(String description) { this.description = description; }
    public void setRating(int rating) { this.rating = rating; }
    public void setImageResource(String imageResource) { this.imageResource = imageResource; }
}

package ac.id.binus.labux.utils;

public class UserSession {
    private static UserSession instance;
    private String username;

    private UserSession() {
        // Private constructor to prevent instantiation
    }

    public static UserSession getInstance() {
        if (instance == null) {
            instance = new UserSession();
        }
        return instance;
    }

    public void setUsername(String username) {
        this.username = username;
    }

    public String getUsername() {
        return username;
    }

    public void clearSession() {
        username = null;
    }
}
