package ac.id.binus.labux;

import android.content.Intent;
import android.os.Bundle;
import android.text.TextUtils;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ImageView;
import android.widget.RelativeLayout;
import android.widget.TextView;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

public class LoginActivity extends AppCompatActivity {

    private EditText etUsername, etPassword;
    private Button btnLogin;
    private TextView tvForgotPassword;
    private View btnGoogleLogin, btnAppleLogin, btnFacebookLogin;
    private RelativeLayout forgotPasswordOverlay;
    private ImageView btnCloseOverlay;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_login);

        // Hide action bar for full screen experience
        if (getSupportActionBar() != null) {
            getSupportActionBar().hide();
        }

        initViews();
        setupClickListeners();
    }

    private void initViews() {
        etUsername = findViewById(R.id.et_username);
        etPassword = findViewById(R.id.et_password);
        btnLogin = findViewById(R.id.btn_login);
        tvForgotPassword = findViewById(R.id.tv_forgot_password);
        btnGoogleLogin = findViewById(R.id.btn_google_login);
        btnAppleLogin = findViewById(R.id.btn_apple_login);
        btnFacebookLogin = findViewById(R.id.btn_facebook_login);
        forgotPasswordOverlay = findViewById(R.id.forgot_password_overlay);
        btnCloseOverlay = findViewById(R.id.btn_close_overlay);
    }

    private void setupClickListeners() {
        btnLogin.setOnClickListener(v -> handleLogin());

        tvForgotPassword.setOnClickListener(v -> handleForgotPassword());

        btnGoogleLogin.setOnClickListener(v -> handleGoogleLogin());

        btnAppleLogin.setOnClickListener(v -> handleAppleLogin());

        btnFacebookLogin.setOnClickListener(v -> handleFacebookLogin());

        // Close overlay listeners
        btnCloseOverlay.setOnClickListener(v -> hideForgotPasswordOverlay());
        forgotPasswordOverlay.setOnClickListener(v -> hideForgotPasswordOverlay());
    }

    private void handleLogin() {
        String username = etUsername.getText().toString().trim();
        String password = etPassword.getText().toString().trim();

        if (TextUtils.isEmpty(username)) {
            etUsername.setError("Username is required");
            etUsername.requestFocus();
            return;
        }

        if (TextUtils.isEmpty(password)) {
            etPassword.setError("Password is required");
            etPassword.requestFocus();
            return;
        }

        // For now, simulate a successful login
        // In a real app, you would validate credentials with your backend
        if (username.equals("admin") && password.equals("password")) {
            Toast.makeText(this, "Login Successful!", Toast.LENGTH_SHORT).show();

            // Navigate to MainActivity
            Intent intent = new Intent(LoginActivity.this, MainActivity.class);
            startActivity(intent);
            finish();
        } else {
            Toast.makeText(this, "Invalid username or password", Toast.LENGTH_SHORT).show();
        }
    }

    private void handleForgotPassword() {
        showForgotPasswordOverlay();
    }

    private void showForgotPasswordOverlay() {
        forgotPasswordOverlay.setVisibility(View.VISIBLE);
    }

    private void hideForgotPasswordOverlay() {
        forgotPasswordOverlay.setVisibility(View.GONE);
    }

    private void handleGoogleLogin() {
        Toast.makeText(this, "Google Login clicked", Toast.LENGTH_SHORT).show();
        // Implement Google OAuth login
    }

    private void handleAppleLogin() {
        Toast.makeText(this, "Apple Login clicked", Toast.LENGTH_SHORT).show();
        // Implement Apple login
    }

    private void handleFacebookLogin() {
        Toast.makeText(this, "Facebook Login clicked", Toast.LENGTH_SHORT).show();
        // Implement Facebook login
    }
}

package ac.id.binus.labux;

import android.content.Intent;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;

import ac.id.binus.labux.utils.UserSession;

public class LoginActivity extends AppCompatActivity {

    private EditText etUsername, etPassword;
    private Button btnLogin;
    private TextView tvUsernameError, tvPasswordError;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_login);

        // Hide action bar for full screen experience
        if (getSupportActionBar() != null) {
            getSupportActionBar().hide();
        }

        initViews();
        setupClickListeners();
    }

    private void initViews() {
        etUsername = findViewById(R.id.et_username);
        etPassword = findViewById(R.id.et_password);
        btnLogin = findViewById(R.id.btn_login);
        tvUsernameError = findViewById(R.id.tv_username_error);
        tvPasswordError = findViewById(R.id.tv_password_error);
    }

    private void setupClickListeners() {
        btnLogin.setOnClickListener(v -> handleLogin());
    }

    private void handleLogin() {
        // Clear previous error messages
        hideErrorMessages();

        String username = etUsername.getText().toString().trim();
        String password = etPassword.getText().toString().trim();

        boolean hasError = false;

        // Validate username
        if (username.isEmpty()) {
            showUsernameError("Username must be filled in");
            hasError = true;
        } else if (username.length() < 5 || username.length() > 10) {
            showUsernameError("Username must be 5-10 characters long");
            hasError = true;
        }

        // Validate password
        if (password.isEmpty()) {
            showPasswordError("Password must not be empty");
            hasError = true;
        }

        // If validation fails, return early
        if (hasError) {
            return;
        }

        // If validation passes, store username and navigate to MainActivity
        UserSession.getInstance().setUsername(username);

        Intent intent = new Intent(LoginActivity.this, MainActivity.class);
        startActivity(intent);
        finish();
    }

    private void showUsernameError(String message) {
        tvUsernameError.setText(message);
        tvUsernameError.setVisibility(View.VISIBLE);
    }

    private void showPasswordError(String message) {
        tvPasswordError.setText(message);
        tvPasswordError.setVisibility(View.VISIBLE);
    }

    private void hideErrorMessages() {
        tvUsernameError.setVisibility(View.GONE);
        tvPasswordError.setVisibility(View.GONE);
    }
}

package ac.id.binus.labux.adapter;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import java.util.List;

import ac.id.binus.labux.R;
import ac.id.binus.labux.model.Anime;

public class AnimeCarouselAdapter extends RecyclerView.Adapter<AnimeCarouselAdapter.CarouselViewHolder> {

    private Context context;
    private List<Anime> animeList;

    public AnimeCarouselAdapter(Context context, List<Anime> animeList) {
        this.context = context;
        this.animeList = animeList;
    }

    @NonNull
    @Override
    public CarouselViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(context).inflate(R.layout.item_anime_carousel, parent, false);
        return new CarouselViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull CarouselViewHolder holder, int position) {
        Anime anime = animeList.get(position);

        // Set anime title and genre
        holder.animeTitle.setText(anime.getTitle());
        holder.animeGenre.setText(anime.getGenre());

        // Load anime image using wide image resource
        int imageResId = context.getResources().getIdentifier(anime.getWideImageResource(), "drawable", context.getPackageName());
        if (imageResId != 0) {
            holder.animeImage.setImageResource(imageResId);
        } else {
            holder.animeImage.setImageResource(R.drawable.featured_anime);
        }
    }

    @Override
    public int getItemCount() {
        return animeList.size();
    }

    public static class CarouselViewHolder extends RecyclerView.ViewHolder {
        ImageView animeImage;
        TextView animeTitle;
        TextView animeGenre;

        public CarouselViewHolder(@NonNull View itemView) {
            super(itemView);
            animeImage = itemView.findViewById(R.id.carousel_image);
            animeTitle = itemView.findViewById(R.id.carousel_title);
            animeGenre = itemView.findViewById(R.id.carousel_genre);
        }
    }
}

